import React, { useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchProducts } from '../store/productSlice';
import { addToWishlist, removeFromWishlist } from '../store/wishlistSlice';

const Home = () => {
  const dispatch = useDispatch();
  const { products, loading } = useSelector(state => state.products);
  const { items: wishlistItems } = useSelector(state => state.wishlist);
  const carouselRef = useRef(null);

  useEffect(() => {
    dispatch(fetchProducts());
  }, [dispatch]);

  const featuredProducts = products.slice(0, 6);

  const isInWishlist = (productId) => {
    return wishlistItems.some(item => item._id === productId);
  };

  const handleWishlistToggle = (product) => {
    if (isInWishlist(product._id)) {
      dispatch(removeFromWishlist(product._id));
    } else {
      dispatch(addToWishlist(product));
    }
  };

  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) return;

    let isDown = false;
    let startX;
    let scrollLeft;

    const handleMouseDown = (e) => {
      isDown = true;
      carousel.classList.add('active');
      startX = e.pageX - carousel.offsetLeft;
      scrollLeft = carousel.scrollLeft;
    };

    const handleMouseLeave = () => {
      isDown = false;
      carousel.classList.remove('active');
    };

    const handleMouseUp = () => {
      isDown = false;
      carousel.classList.remove('active');
    };

    const handleMouseMove = (e) => {
      if (!isDown) return;
      e.preventDefault();
      const x = e.pageX - carousel.offsetLeft;
      const walk = (x - startX) * 2;
      carousel.scrollLeft = scrollLeft - walk;
    };

    carousel.addEventListener('mousedown', handleMouseDown);
    carousel.addEventListener('mouseleave', handleMouseLeave);
    carousel.addEventListener('mouseup', handleMouseUp);
    carousel.addEventListener('mousemove', handleMouseMove);

    return () => {
      carousel.removeEventListener('mousedown', handleMouseDown);
      carousel.removeEventListener('mouseleave', handleMouseLeave);
      carousel.removeEventListener('mouseup', handleMouseUp);
      carousel.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <section className="hero">
        <div className="container">
          <div className="sale-badge">🎉 50% Festive Sale</div>
          <h1>Elevate Your Shopping Experience Today!</h1>
          <p>Discover amazing products with up to 50% off in our festive sale</p>
          <Link to="/products" className="btn btn-primary">Shop Now</Link>
        </div>
      </section>

      <div className="container">
        <section className="carousel-section">
          <div className="container">
            <h2>New Collection</h2>
          </div>
          <div className="carousel-container">
            <button className="scroll-btn left" onClick={() => carouselRef.current.scrollBy({left: -300, behavior: 'smooth'})}>
              ‹
            </button>
            <div className="product-carousel" ref={carouselRef}>
            {featuredProducts.map(product => (
              <div key={product._id} className="product-card">
                <div className="product-image-container">
                  <img 
                    src={product.image} 
                    alt={product.name}
                    onError={(e) => {
                      console.log('Image failed to load:', product.image);
                      e.target.src = 'https://via.placeholder.com/300x400/cccccc/666666?text=No+Image';
                    }}
                  />
                  <button 
                    className={`wishlist-btn ${isInWishlist(product._id) ? 'active' : ''}`}
                    onClick={() => handleWishlistToggle(product)}
                  >
                    ♥
                  </button>
                </div>
                <div className="product-card-content">
                  <h3>{product.name}</h3>
                  <div className="product-rating">
                    <span className="star">★</span>
                    <span className="star">★</span>
                    <span className="star">★</span>
                    <span className="star">★</span>
                    <span className="star">★</span>
                  </div>
                  <p>${product.price}</p>
                  <Link to={`/product/${product._id}`} className="btn btn-primary">
                    View Details
                  </Link>
                </div>
              </div>
            ))}
            </div>
            <button className="scroll-btn right" onClick={() => carouselRef.current.scrollBy({left: 300, behavior: 'smooth'})}>
              ›
            </button>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Home;
