import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProductById } from '../store/productSlice';
import { addToCart } from '../store/cartSlice';

const ProductDetail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { currentProduct, loading } = useSelector(state => state.products);
  const { user } = useSelector(state => state.auth);
  const [selectedSize, setSelectedSize] = useState('');

  useEffect(() => {
    dispatch(fetchProductById(id));
  }, [dispatch, id]);

  const handleAddToCart = () => {
    if (!user) {
      alert('Please login to add items to cart');
      return;
    }
    dispatch(addToCart({
      productId: currentProduct._id,
      name: currentProduct.name,
      price: currentProduct.price,
      image: currentProduct.image,
      quantity: 1
    }));
    alert('Added to cart!');
  };

  if (loading) return <div>Loading...</div>;
  if (!currentProduct) return <div>Product not found</div>;

  return (
    <div className="container">
      <div style={{ display: 'flex', gap: '2rem', padding: '2rem 0' }}>
        <img 
          src={currentProduct.image} 
          alt={currentProduct.name}
          style={{ width: '400px', height: '400px', objectFit: 'cover' }}
        />
        <div>
          <h1>{currentProduct.name}</h1>
          <p style={{ fontSize: '1.5rem', color: '#007bff', margin: '1rem 0' }}>
            ${currentProduct.price}
          </p>
          <p>{currentProduct.description}</p>
          <p>Stock: {currentProduct.stock}</p>

          <div style={{ margin: '1rem 0' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '500' }}>
              Select Size:
            </label>
            <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
              {currentProduct.sizes?.map(size => (
                <button
                  key={size}
                  onClick={() => setSelectedSize(size)}
                  style={{
                    padding: '0.5rem 1rem',
                    border: selectedSize === size ? '2px solid #007bff' : '1px solid #ddd',
                    background: selectedSize === size ? '#007bff' : 'white',
                    color: selectedSize === size ? 'white' : '#333',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    minWidth: '50px'
                  }}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>

          <button 
            onClick={handleAddToCart}
            className="btn btn-primary"
            style={{ marginTop: '1rem' }}
            disabled={!selectedSize}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
