import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { cancelOrder } from '../store/ordersSlice';

const Orders = () => {
  const { orders } = useSelector(state => state.orders);
  const dispatch = useDispatch();

  const handleCancelOrder = (orderId) => {
    if (window.confirm('Are you sure you want to cancel this order?')) {
      dispatch(cancelOrder(orderId));
    }
  };

  const getShipmentSteps = (status) => {
    const steps = ['Processing', 'Shipped', 'In Transit', 'Delivered'];
    const currentIndex = steps.indexOf(status);
    return steps.map((step, index) => ({
      name: step,
      completed: index <= currentIndex,
      active: index === currentIndex
    }));
  };

  const generateShipmentStatus = (orderId) => {
    const seed = orderId.toString().split('').reduce((a, b) => parseInt(a) + parseInt(b), 0);
    const statuses = ['Processing', 'Shipped', 'In Transit', 'Delivered'];
    return statuses[seed % statuses.length];
  };

  if (orders.length === 0) {
    return (
      <div className="container">
        <h2>Your Orders</h2>
        <div style={{ textAlign: 'center', padding: '3rem' }}>
          <p>No orders found. Start shopping to see your orders here!</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <h2>Your Orders</h2>
      <div className="orders-list">
        {orders.map(order => (
          <div key={order.id} className="order-card">
            <div className="order-header">
              <h3>Order #{order.id}</h3>
              <div className="order-info">
                <span className="order-date">
                  {new Date(order.date).toLocaleDateString()}
                </span>
                <span className={`order-status ${order.status.toLowerCase()}`}>{order.status}</span>
                {order.status === 'Completed' && (
                  <button 
                    onClick={() => handleCancelOrder(order.id)}
                    className="btn btn-cancel"
                  >
                    Cancel Order
                  </button>
                )}
              </div>
            </div>
            <div className="order-items">
              {order.items.map(item => (
                <div key={item._id} className="order-item">
                  <img src={item.image} alt={item.name} />
                  <div className="item-details">
                    <h4>{item.name}</h4>
                    <p>Quantity: {item.quantity}</p>
                    <p>${item.price}</p>
                  </div>
                </div>
              ))}
            </div>
            <div className="order-total">
              <strong>Total: ${order.total}</strong>
            </div>
            <div className="tracking-section">
              <div className="tracking-header">
                <h4>Shipment Tracking</h4>
                <span className="tracking-id">ID: {order.trackingId || `TRK${order.id.toString().slice(-8)}`}</span>
              </div>
              <div className="tracking-progress">
                {getShipmentSteps(generateShipmentStatus(order.id)).map((step, index) => (
                  <div key={index} className={`tracking-step ${step.completed ? 'completed' : ''} ${step.active ? 'active' : ''}`}>
                    <div className="step-icon">
                      {step.completed ? '✓' : index + 1}
                    </div>
                    <span className="step-name">{step.name}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Orders;
