import React, { useEffect, useState, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProductById, fetchProducts } from '../store/productSlice';
import { addToCart } from '../store/cartSlice';
import { addToWishlist, removeFromWishlist } from '../store/wishlistSlice';

const ProductDetail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { currentProduct, products, loading } = useSelector(state => state.products);
  const { user } = useSelector(state => state.auth);
  const { items: wishlistItems } = useSelector(state => state.wishlist);
  const [selectedSize, setSelectedSize] = useState('');
  const relatedCarouselRef = useRef(null);

  useEffect(() => {
    dispatch(fetchProductById(id));
    dispatch(fetchProducts());
  }, [dispatch, id]);

  const relatedProducts = products.filter(product => 
    product.category === currentProduct?.category && product._id !== currentProduct?._id
  ).slice(0, 4);

  const isInWishlist = (productId) => {
    return wishlistItems.some(item => item._id === productId);
  };

  const handleWishlistToggle = (product) => {
    if (isInWishlist(product._id)) {
      dispatch(removeFromWishlist(product._id));
    } else {
      dispatch(addToWishlist(product));
    }
  };

  const handleAddToCart = () => {
    if (!user) {
      alert('Please login to add items to cart');
      return;
    }
    dispatch(addToCart({
      productId: currentProduct._id,
      name: currentProduct.name,
      price: currentProduct.price,
      image: currentProduct.image,
      quantity: 1
    }));
    alert('Added to cart!');
  };

  if (loading) return <div>Loading...</div>;
  if (!currentProduct) return <div>Product not found</div>;

  return (
    <div className="container">
      <div style={{ display: 'flex', gap: '2rem', padding: '2rem 0' }}>
        <img 
          src={currentProduct.image} 
          alt={currentProduct.name}
          style={{ width: '400px', height: '400px', objectFit: 'cover' }}
        />
        <div>
          <h1>{currentProduct.name}</h1>
          <p style={{ fontSize: '1.5rem', color: '#007bff', margin: '1rem 0' }}>
            ${currentProduct.price}
          </p>
          <p>{currentProduct.description}</p>
          <p>Stock: {currentProduct.stock}</p>

          <div style={{ margin: '1rem 0' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '500' }}>
              Select Size:
            </label>
            <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
              {currentProduct.sizes?.map(size => (
                <button
                  key={size}
                  onClick={() => setSelectedSize(size)}
                  style={{
                    padding: '0.5rem 1rem',
                    border: selectedSize === size ? '2px solid #007bff' : '1px solid #ddd',
                    background: selectedSize === size ? '#007bff' : 'white',
                    color: selectedSize === size ? 'white' : '#333',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    minWidth: '50px'
                  }}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>

          <button 
            onClick={handleAddToCart}
            className="btn btn-primary"
            style={{ marginTop: '1rem' }}
            disabled={!selectedSize}
          >
            Add to Cart
          </button>
        </div>
      </div>

      {relatedProducts.length > 0 && (
        <section className="carousel-section" style={{ marginTop: '3rem' }}>
          <div className="container">
            <h3 style={{ 
              fontFamily: 'Playfair Display, serif',
              fontSize: '2rem',
              fontWeight: '600',
              textAlign: 'center',
              marginBottom: '2rem',
              color: '#333'
            }}>Related Products</h3>
          </div>
          <div className="carousel-container">
            <button 
              className="scroll-btn left" 
              onClick={() => relatedCarouselRef.current.scrollBy({left: -300, behavior: 'smooth'})}
            >
              ‹
            </button>
            <div className="product-carousel" ref={relatedCarouselRef}>
              {relatedProducts.map(product => (
                <div key={product._id} className="product-card">
                  <div className="product-image-container">
                    <img src={product.image} alt={product.name} />
                    <button 
                      className={`wishlist-btn ${isInWishlist(product._id) ? 'active' : ''}`}
                      onClick={() => handleWishlistToggle(product)}
                    >
                      ♥
                    </button>
                  </div>
                  <div className="product-card-content">
                    <h3>{product.name}</h3>
                    <div className="product-rating">
                      <span className="star">★</span>
                      <span className="star">★</span>
                      <span className="star">★</span>
                      <span className="star">★</span>
                      <span className="star">★</span>
                    </div>
                    <p>${product.price}</p>
                    <Link to={`/product/${product._id}`} className="btn btn-primary">
                      View Details
                    </Link>
                  </div>
                </div>
              ))}
            </div>
            <button 
              className="scroll-btn right" 
              onClick={() => relatedCarouselRef.current.scrollBy({left: 300, behavior: 'smooth'})}
            >
              ›
            </button>
          </div>
        </section>
      )}
    </div>
  );
};

export default ProductDetail;
