import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../store/authSlice';
import { fetchProducts } from '../store/productSlice';

const Header = () => {
  const { user } = useSelector(state => state.auth);
  const { items } = useSelector(state => state.cart);
  const [searchTerm, setSearchTerm] = useState('');
  const [showCategories, setShowCategories] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleLogout = () => {
    dispatch(logout());
    navigate('/');
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/products?search=${encodeURIComponent(searchTerm)}`);
    } else {
      navigate('/products');
    }
  };

  const handleCategoryClick = (category) => {
    navigate(`/products?search=${encodeURIComponent(category)}`);
    setShowCategories(false);
  };

  return (
    <header className="header">
      <div className="container">
        <nav className="nav">
          <Link to="/">
            <h1>Memu Store !</h1>
          </Link>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li className="dropdown">
              <button 
                onClick={() => setShowCategories(!showCategories)}
                className="dropdown-btn"
              >
                Categories
              </button>
              {showCategories && (
                <div className="dropdown-menu">
                  <div onClick={() => handleCategoryClick('Skirt')}>Skirts</div>
                  <div onClick={() => handleCategoryClick('Kurti')}>Kurtis</div>
                  <div onClick={() => handleCategoryClick('Top')}>Tops</div>
                  <div onClick={() => handleCategoryClick('Saree')}>Sarees</div>
                  <div onClick={() => handleCategoryClick('Dress')}>Dresses</div>
                  <div onClick={() => handleCategoryClick('Jacket')}>Jackets</div>
                </div>
              )}
            </li>
            <li><Link to="/products">More</Link></li>
          </ul>
          <div className="nav-icons">
            <form onSubmit={handleSearch}>
              <input 
                type="text" 
                placeholder="Search products..." 
                className="search-bar"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </form>
            <Link to="/cart">ðŸ›’ ({items.length})</Link>
            <Link to="/wishlist">Wishlist</Link>
            {user ? (
              <>
                <span>Hi, {user.name}</span>
                <button onClick={handleLogout}>Logout</button>
              </>
            ) : (
              <>
                <Link to="/login">Login</Link>
                <Link to="/register">Register</Link>
              </>
            )}
          </div>
        </nav>
      </div>
    </header>
  );
};

export default Header;
